mapper.select:

select * from employee
where emp_id=#{empId} 
and emp_yn='N'

mapper.update:
update employee
	set email=#{email},  phone=#{phone}, address=#{address}
where   emp_id=#{empId}

mapper.delete:
update employee
set emp_yn='Y'
where emp_id=#{empId}

mapper.select:
select emp_id
from employee
where emp_name=#{empName}

mapper.update:
UPDATE EMP_ATTACHMENT
SET ORIGIN_NAME=#{originName}, CHANGE_NAME=#{changeName}
WHERE EMP_NO=#{empNO}

mapper.select:

		select *
		from employee e
		left outer join job j on (e.job_code=j.job_code)	
		left outer join department d on (e.dept_code=d.dept_code)	
		where emp_yn='N'

    		AND j.job_code=#{job}


    		AND d.dept_code=#{dept}


    		AND e.emp_name=#{emp}

mapper.update:
update employee
set dept_code=#{deptCode},  job_code=#{jobCode}, salary=#{salary}
where   emp_no=#{empNO}

update employee
set emp_yn='Y'
where emp_id=#{empId}
