mapper.update:

UPDATE LEAVE
SET LEAVE_ISUSE = 'Y', LEAVE_USEDATE = #{leaveUseDate}
WHERE LEAVE_NO IN (SELECT LEAVE_NO
                  FROM (SELECT LEAVE_NO
                        FROM LEAVE
                        WHERE LEAVE_ISUSE = 'N'
                        AND EMP_NO = #{eno}
                        ORDER BY LEAVE_NO
                        )
                 WHERE ROWNUM <= 1
                )
